// frontend/src/components/AdminDashboard.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

// (StatsCards and UserList components remain the same as before)
// --- Stats Cards Component ---
const StatsCards = ({ userCount, classCount }) => ( <div/> /* ...same as before... */ );
// --- User List Component ---
const UserList = ({ users, onDelete }) => ( <div/> /* ...same as before... */ );


// --- NEW & IMPROVED Add Class Form Component ---
const AddClassForm = ({ onClassAdded }) => {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        category: 'Yoga',
        schedule: '',
        duration: 60,
        price: 20,
        instructor: '',
        imageUrl: ''
    });

    const { name, description, category, schedule, duration, price, instructor, imageUrl } = formData;
    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = async e => {
        e.preventDefault();
        try {
            // Note: The backend route is relative now thanks to the proxy
            const res = await axios.post('/api/classes', formData);
            alert('Class added successfully!');
            onClassAdded(res.data);
            // Clear form
            setFormData({ name: '', description: '', category: 'Yoga', schedule: '', duration: 60, price: 20, instructor: '', imageUrl: '' });
        } catch (err) {
            console.error(err.response.data);
            alert('Error adding class.');
        }
    };

    return (
        <div className="card mt-4">
            <div className="card-header">
                <h3>Add New Class</h3>
            </div>
            <div className="card-body">
                <form onSubmit={onSubmit}>
                    <div className="row">
                        <div className="col-md-6 mb-3">
                            <input type="text" className="form-control" placeholder="Class Name" name="name" value={name} onChange={onChange} required />
                        </div>
                        <div className="col-md-6 mb-3">
                            <input type="text" className="form-control" placeholder="Instructor" name="instructor" value={instructor} onChange={onChange} required />
                        </div>
                    </div>
                    <div className="mb-3">
                        <textarea className="form-control" placeholder="Description" name="description" value={description} onChange={onChange} required></textarea>
                    </div>
                    <div className="row">
                        <div className="col-md-6 mb-3">
                            <label>Category</label>
                            <select name="category" value={category} onChange={onChange} className="form-select">
                                <option value="Yoga">Yoga</option>
                                <option value="Gym">Gym</option>
                                <option value="Dance">Dance</option>
                                <option value="Zumba">Zumba</option>
                            </select>
                        </div>
                        <div className="col-md-6 mb-3">
                            <label>Schedule</label>
                            <input type="datetime-local" className="form-control" name="schedule" value={schedule} onChange={onChange} required />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-6 mb-3">
                            <label>Duration (in minutes)</label>
                            <input type="number" className="form-control" placeholder="60" name="duration" value={duration} onChange={onChange} required />
                        </div>
                        <div className="col-md-6 mb-3">
                            <label>Price ($)</label>
                            <input type="number" className="form-control" placeholder="20" name="price" value={price} onChange={onChange} required />
                        </div>
                    </div>
                    <div className="mb-3">
                        <input type="text" className="form-control" placeholder="Image URL" name="imageUrl" value={imageUrl} onChange={onChange} />
                    </div>
                    <button type="submit" className="btn btn-success w-100">Add Class</button>
                </form>
            </div>
        </div>
    );
};


// --- Main Admin Dashboard Component (with updated API calls) ---
function AdminDashboard() {
    // ... state declarations are the same ...
    const [users, setUsers] = useState([]);
    const [classes, setClasses] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // The routes are now relative and will use the proxy
                const [usersRes, classesRes] = await Promise.all([
                    axios.get('/api/users'),
                    axios.get('/api/classes')
                ]);
                setUsers(usersRes.data);
                setClasses(classesRes.data);
            } catch (err) {
                console.error("Error fetching admin data", err);
            }
        };
        fetchData();
    }, []);

    // ... handleDeleteUser and handleClassAdded are the same ...
    const handleDeleteUser = async (id) => {
        if(window.confirm('Are you sure?')){
            await axios.delete(`/api/users/${id}`);
            setUsers(users.filter(u => u._id !== id));
        }
    };
    const handleClassAdded = (newClass) => setClasses([...classes, newClass]);


    return (
        <div>
            {/* ... other JSX is the same ... */}
            <h2 className="mb-4">Admin Dashboard</h2>
            <StatsCards userCount={users.length} classCount={classes.length} />
            <UserList users={users} onDelete={handleDeleteUser} />
            <AddClassForm onClassAdded={handleClassAdded} />
        </div>
    );
}

export default AdminDashboard;
