// frontend/src/components/ClassList.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import formatDate from '../utils/formatDate';

const DEFAULT_IMAGE = 'https://images.unsplash.com/photo-1540496905039-512d2f7550f8?auto=format&fit=crop&q=60&w=500';
const CATEGORIES = ["All", "Yoga", "Gym", "Dance", "Zumba"]; // Define categories for the filter

function ClassList() {
    const [classes, setClasses] = useState([]);
    const { user } = useAuth();
    // --- NEW STATE FOR FILTERING ---
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('All');

    useEffect(() => {
        axios.get('/api/classes').then(response => {
            setClasses(response.data);
        }).catch(error => {
            console.error('There was an error fetching the classes!', error);
        });
    }, []);

    const now = new Date();

    // --- FILTERING LOGIC ---
    const filteredClasses = classes
        .filter(cls => 
            cls.name.toLowerCase().includes(searchTerm.toLowerCase())
        )
        .filter(cls => 
            selectedCategory === 'All' || cls.category === selectedCategory
        );

    return (
        <div>
            <h2 className="mb-4" style={{ color: 'var(--primary-color)' }}>Class Schedule</h2>

            {/* --- NEW FILTER AND SEARCH UI --- */}
            <div className="row mb-4">
                <div className="col-md-8">
                    <input 
                        type="text" 
                        className="form-control" 
                        placeholder="Search for a class..." 
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>
                <div className="col-md-4">
                    <select 
                        className="form-select"
                        value={selectedCategory}
                        onChange={(e) => setSelectedCategory(e.target.value)}
                    >
                        {CATEGORIES.map(cat => <option key={cat} value={cat}>{cat}</option>)}
                    </select>
                </div>
            </div>

            <div className="row">
                {filteredClasses.length > 0 ? filteredClasses.map(cls => {
                    const classDate = new Date(cls.schedule);
                    const isPast = classDate < now;
                    const isFull = cls.enrollmentCount >= cls.capacity;

                    return (
                        <div className="col-md-4 mb-4" key={cls._id}>
                            {/* ... (The card JSX is the same as the previous correct version) ... */}
                        </div>
                    );
                }) : <p>No classes match your search criteria.</p>}
            </div>
        </div>
    );
}

export default ClassList;
