// frontend/src/components/AdminDashboard.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

// --- Stats Cards Component ---
const StatsCards = ({ userCount, classCount }) => (
    <div className="row mb-4">
        <div className="col-md-6">
            <div className="card text-white bg-primary mb-3">
                <div className="card-header">Total Users</div>
                <div className="card-body">
                    <h5 className="card-title display-4">{userCount}</h5>
                </div>
            </div>
        </div>
        <div className="col-md-6">
            <div className="card text-white bg-success mb-3">
                <div className="card-header">Total Classes</div>
                <div className="card-body">
                    <h5 className="card-title display-4">{classCount}</h5>
                </div>
            </div>
        </div>
    </div>
);

// --- User List Component ---
const UserList = ({ users, onDelete }) => (
    <div className="card">
        <div className="card-header">
            <h3>User Management</h3>
        </div>
        <div className="card-body">
            <div className="table-responsive">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Username</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id}>
                                <td>{user.username}</td>
                                <td>{user.email}</td>
                                <td>{user.role}</td>
                                <td>
                                    <button className="btn btn-danger btn-sm" onClick={() => onDelete(user._id)}>
                                        <i className="fas fa-trash"></i>
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    </div>
);

// --- Add Class Form Component ---
const AddClassForm = ({ onClassAdded }) => {
    const [formData, setFormData] = useState({
        name: '', description: '', category: 'Yoga', 
        schedule: '', duration: 60, price: 20, instructor: ''
    });

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = async e => {
        e.preventDefault();
        try {
            const res = await axios.post('/api/classes', formData);
            alert('Class added successfully!');
            onClassAdded(res.data);
            // Clear form
            setFormData({ name: '', description: '', category: 'Yoga', schedule: '', duration: 60, price: 20, instructor: '' });
        } catch (err) {
            alert('Error adding class.');
        }
    };

    return (
        <div className="card mt-4">
            <div className="card-header">
                <h3>Add New Class</h3>
            </div>
            <div className="card-body">
                <form onSubmit={onSubmit}>
                    {/* Form fields */}
                    <div className="mb-3">
                        <input type="text" className="form-control" placeholder="Class Name" name="name" value={formData.name} onChange={onChange} required />
                    </div>
                    {/* Add other fields for description, category, schedule, etc. */}
                    <button type="submit" className="btn btn-success w-100">Add Class</button>
                </form>
            </div>
        </div>
    );
};


// --- Main Admin Dashboard Component ---
function AdminDashboard() {
    const { user } = useAuth();
    const [users, setUsers] = useState([]);
    const [classes, setClasses] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch all data in parallel
                const [usersRes, classesRes] = await Promise.all([
                    axios.get('/api/users'),
                    axios.get('/api/classes')
                ]);
                setUsers(usersRes.data);
                setClasses(classesRes.data);
                setLoading(false);
            } catch (err) {
                console.error("Error fetching admin data", err);
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    const handleDeleteUser = async (id) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await axios.delete(`/api/users/${id}`);
                setUsers(users.filter(u => u._id !== id));
                alert('User deleted successfully');
            } catch (err) {
                alert('Error deleting user');
            }
        }
    };

    const handleClassAdded = (newClass) => {
        setClasses([...classes, newClass]);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2 className="mb-4">Admin Dashboard</h2>
            <div className="alert alert-info">
                Welcome, {user?.username}! You are logged in as an Administrator.
            </div>
            
            <StatsCards userCount={users.length} classCount={classes.length} />
            <UserList users={users} onDelete={handleDeleteUser} />
            <AddClassForm onClassAdded={handleClassAdded} />
        </div>
    );
}

export default AdminDashboard;
