// frontend/src/components/AdminDashboard.js

import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useAuth } from '../context/AuthContext'
import { useNotification } from '../context/NotificationContext' // 1. IMPORT
import formatDate from '../utils/formatDate'

// --- Add Class Form Component (UPDATED) ---
const AddClassForm = ({ onClassAdded }) => {
  const { showNotification } = useNotification() // GET THE FUNCTION
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: 'Yoga',
    schedule: '',
    duration: 60,
    price: 20,
    instructor: '',
    capacity: 15,
  })
  const [file, setFile] = useState(null)
  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value })
  const onFileChange = e => setFile(e.target.files[0])

  const onSubmit = async e => {
    e.preventDefault()
    const data = new FormData()
    if (file) {
      data.append('image', file)
    }
    for (const key in formData) {
      data.append(key, formData[key])
    }
    try {
      const config = { headers: { 'Content-Type': 'multipart/form-data' } }
      const res = await axios.post('/api/classes', data, config)
      showNotification('Class added successfully!') // USE NOTIFICATION
      onClassAdded(res.data)
      setFormData({
        name: '',
        description: '',
        category: 'Yoga',
        schedule: '',
        duration: 60,
        price: 20,
        instructor: '',
        capacity: 15,
      })
      setFile(null)
    } catch (err) {
      const errorMsg =
        err.response?.data?.message || 'Error adding class.'
      showNotification(errorMsg, 'error') // USE NOTIFICATION
    }
  }

  return (
    <div className="card mt-4">
      {/* ... (The form JSX is unchanged) ... */}
    </div>
  )
}

// --- Edit Class Modal Component (UPDATED) ---
const EditClassModal = ({ show, handleClose, classData, onClassUpdated }) => {
  const { showNotification } = useNotification() // GET THE FUNCTION
  const [formData, setFormData] = useState({})
  const [file, setFile] = useState(null)

  useEffect(() => {
    if (classData) {
      const scheduleDate = classData.schedule
        ? new Date(classData.schedule).toISOString().slice(0, 16)
        : ''
      setFormData({ ...classData, schedule: scheduleDate })
    }
  }, [classData])

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value })
  const onFileChange = e => setFile(e.target.files[0])

  const onSubmit = async e => {
    e.preventDefault()
    const data = new FormData()
    if (file) data.append('image', file)
    for (const key in formData) {
      data.append(key, formData[key])
    }
    try {
      const res = await axios.put(`/api/classes/${classData._id}`, data)
      showNotification('Class updated successfully!') // USE NOTIFICATION
      onClassUpdated(res.data)
      handleClose()
    } catch (err) {
      showNotification('Error updating class.', 'error') // USE NOTIFICATION
    }
  }

  if (!show) return null
  return (
    <div
      className="modal show"
      style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}
    >
      {/* ... (The modal JSX is unchanged) ... */}
    </div>
  )
}

// --- Main Admin Dashboard Component (UPDATED) ---
function AdminDashboard() {
  const { showNotification } = useNotification() // 2. GET THE FUNCTION
  const { user } = useAuth()
  const [users, setUsers] = useState([])
  const [classes, setClasses] = useState([])
  const [adminBookings, setAdminBookings] = useState([])
  const [showEditModal, setShowEditModal] = useState(false)
  const [selectedClass, setSelectedClass] = useState(null)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [usersRes, classesRes, bookingsRes] = await Promise.all([
          axios.get('/api/users'),
          axios.get('/api/classes'),
          axios.get('/api/bookings/mybookings'),
        ])
        setUsers(usersRes.data)
        setClasses(classesRes.data)
        setAdminBookings(bookingsRes.data)
      } catch (err) {
        console.error('Error fetching admin data', err)
        showNotification('Failed to load dashboard data.', 'error')
      }
    }
    fetchData()
  }, [])

  const handleDeleteUser = async id => {
    if (
      window.confirm(
        'Are you sure you want to delete this user? This cannot be undone.'
      )
    ) {
      try {
        await axios.delete(`/api/users/${id}`)
        setUsers(users.filter(u => u._id !== id))
        showNotification('User deleted successfully!') // USE NOTIFICATION
      } catch (err) {
        showNotification('Error deleting user.', 'error') // USE NOTIFICATION
      }
    }
  }

  const handleClassAdded = newClass => {
    setClasses([...classes, newClass])
  }

  const handleClassUpdated = updatedClass => {
    setClasses(
      classes.map(cls => (cls._id === updatedClass._id ? updatedClass : cls))
    )
  }

  const handleDeleteClass = async id => {
    if (window.confirm('Are you sure you want to delete this class?')) {
      try {
        await axios.delete(`/api/classes/${id}`)
        setClasses(classes.filter(cls => cls._id !== id))
        showNotification('Class deleted successfully!') // USE NOTIFICATION
      } catch (err) {
        showNotification('Error deleting class.', 'error') // USE NOTIFICATION
      }
    }
  }

  const handleEditClick = cls => {
    setSelectedClass(cls)
    setShowEditModal(true)
  }

  const handleCloseModal = () => {
    setShowEditModal(false)
    setSelectedClass(null)
  }

  return (
    <div>
      <h2 className="mb-4">Admin Dashboard</h2>
      <div className="alert alert-info">
        Welcome, {user?.username}! Manage your site from here.
      </div>
      {/* ... (All sub-components are rendered here as before) ... */}
    </div>
  )
}

export default AdminDashboard;
