// frontend/src/App.js

import React, { useEffect } from 'react'; // <-- Make sure useEffect is imported
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home';
import ClassList from './components/ClassList';
import AdminDashboard from './components/AdminDashboard';
import UserDashboard from './components/UserDashboard';
import Login from './components/Login';
import Register from './components/Register';
import ProtectedRoute from './components/ProtectedRoute';
import Footer from './components/Footer';
import BookingPage from './components/BookingPage';
import setAuthToken from './utils/setAuthToken';
import './App.css';

function App() {
    // vvv THIS IS THE KEY CHANGE vvv
    useEffect(() => {
        // This effect will run once when the app component loads.
        // It checks if a token exists in localStorage and sets it for all future axios requests.
        const token = localStorage.getItem('token');
        if (token) {
            setAuthToken(token);
        }
    }, []); // The empty dependency array ensures it runs only once.
    // ^^^ END OF KEY CHANGE ^^^

    return (
        <Router>
            <Navbar />
            <div className="container mt-5">
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/classes" element={<ClassList />} />
                    <Route path="/login" element={<Login />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/book/:id" element={<ProtectedRoute allowedRoles={['user', 'admin']}><BookingPage /></ProtectedRoute>} />
                    <Route path="/dashboard" element={<ProtectedRoute allowedRoles={['user', 'admin']}><UserDashboard /></ProtectedRoute>} />
                    <Route path="/admin" element={<ProtectedRoute allowedRoles={['admin']}><AdminDashboard /></ProtectedRoute>} />
                </Routes>
            </div>
            <Footer />
        </Router>
    );
}

export default App;
